import React from 'react'
import './Bar.css'
import Image from "../images/ukulele.jpg"
import {FaBackward} from "react-icons/fa";
import {FaForward} from "react-icons/fa";
import {FaPlay} from "react-icons/fa";
import {FaStop} from "react-icons/fa";

const ActionBar = () => {
    const musicContainer = document.querySelector('.music-container')
    const playBtn = document.querySelector('#play')
    const prevBtn = document.querySelector('#prev')
    const nextBtn = document.querySelector('#next')
    const audio = document.querySelector('#audio')
    const progressContainer = document.querySelector('.progress-container')
    const title = document.querySelector('#title')
    const cover =document.querySelector('#cover')

    // Songs titles
    const songs = ['hey','summer', 'ukulele']

    let songIndex = 2

    //Initially load song info DOM
    // loadSong(songs[songIndex])

    //Update song details
    function loadSong(song){
        title.innerText = song
        audio.src = 'music/${song}.mp4'
        cover.src = 'images/${song}.jpg'
    }


    // Event Listeners


  return (
    <div>

        <div className='action-container play'>
            <div className='music-info'>
            <h4 id='title'> Hey!</h4>
            <div className="progress-container">
                <div className="progress"></div>
            </div>
            </div>
            <div className='image-container'>
                <img src={Image} className='image' id='image'/>
            </div>

            <button width="10px" height="10px" id='backward' className='action-btn'>
                <FaBackward />
            </button>
            <button width="10px" height="10px" id='backward' className='action-btn action-btn-big'>
                <FaPlay width="20px" height="20px" id='play'/>
            </button>
            <button width="10px" height="10px" id='backward' className='action-btn'>
                <FaForward width="10px" height="10px" id='forward'/>
            </button>

        </div>

    </div>
  )
}

export default ActionBar
